import react, { Component } from 'react';
import '../App.css';
import axios from 'axios';


export default class Modal extends Component {
    // state ={
    //     x:0,
    //     doctor: [],
    //     slot: ""
    // };

    componentDidMount() {
        axios.get('http://localhost:5000/doctorList')
            .then(res => {
                this.setState({doctor: res.data});

            }) 
            .catch((error) => {
                console.log(error);
            })
            axios.get('http://localhost:5000/doctorList/611d138bbfd3d737a0012122')
            .then(res => {
                this.setState({slot: res.data});

            })         
          
        } 

    slots = ["4:00 pm - 4:15 pm", "4:15 pm - 4:30 pm", "4:31 pm - 4:45 pm", "4:46 pm - 5:00 pm","5:00pm - 6:00pm "]
    render() {
        return (
            <div className="modal display-block">
                <section className="modal-main">
                    <div className="main-content">
                        <div className="modal-header">
                            <h1>Book Meeting for 15 minute</h1>
                            <button className="modal-button" type="button" onClick={this.props.handleClose}>
                                Close
                            </button>
                        </div>
                        <div className="slotContainer">
                            {this.slots.map((slot =>
                                <div className="slot">{slot}</div>
                            ))
                            }
                            <p>{this.slot}</p>
                        </div>
                    </div>

                </section>
            </div >
        );
    }
};
