import react, { Component } from 'react';
import '../App.css';
import axios from 'axios';


export default class Modal extends Component {
    state ={
        x:0,
        doctor: [],
        slot: [],
    };

    componentDidMount() {
        
            axios.get(`http://localhost:5000/doctorList/${this.props.doctorId}`)
            .then(res => {
                console.log(res)
                this.setState({slot: res.data.slot});

            })         
          
        } 
    bookAppointment = (slotId)=>{
        console.log(slotId);
        const appoint = {
            timing : 10,
            patientName: 'Raman',
            pEmail: 'aman@123'
        }
        axios.put(`http://localhost:5000/doctorList/${this.props.doctorId}`, appoint).
        then(res =>{
            console.log(res);
        })
    }    
        

    render() {
        return (
            <div className="modal display-block">
                <section className="modal-main">
                    <div className="main-content">
                        <div className="modal-header">
                            <h1>Book Meeting for 15 minute</h1>
                            <button className="modal-button" type="button" onClick={this.props.handleClose}>
                                Close
                            </button>
                        </div>
                        <div className="slotContainer">
                            {this.state.slot.map((slot =>
                                <button className="bookSlot" onClick= {this.bookAppointment(slot._id)}><span>{slot.time}</span></button>

                            ))
                            }
                            
                        </div>
                    </div>

                </section>
            </div >
        );
    }
};
