import React, { Component } from 'react';
import '../App.css';
import Navbar from '../components/navbar';
import axios from 'axios';

export default class signup extends Component {


    constructor(props) {
        super(props);
    
        this.onChangeFirstName = this.onChangeFirstName.bind(this);
        this.onChangeLastName = this.onChangeLastName.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    
        this.state = {
          firstName: '',
          lastName: '',
          email: '',
          password: ''        
        }
      }
    
      // componentDidMount() {
      //   axios.get('http://localhost:5000/users/')
      //     .then(response => {
      //       if (response.data.length > 0) {
      //         this.setState({
      //           users: response.data.map(user => user.username),
      //           username: response.data[0].username
      //         })
      //       }
      //     })
      //     .catch((error) => {
      //       console.log(error);
      //     })
    
      // }
    
      onChangeFirstName(e) {
        this.setState({
          firstName: e.target.value
        })
      }
    
      onChangeLastName(e) {
        this.setState({
          lastName: e.target.value
        })
      }
    
      onChangeEmail(e) {
        this.setState({
          email: e.target.value
        })
      }
    
      onChangePassword(e) {
        this.setState({
          password: e.target.value
        })
      }
    
      onSubmit(e) {
        e.preventDefault();
    
        const user = {
          firstName: this.state.firstName,
          lastName: this.state.lastName,
          email: this.state.email,
          password: this.state.password
        }
    
        // console.log(user);
    
        axios.post('http://localhost:5000/newUser/add', user)
          .then(res => console.log(res.data));
    
        window.location = '/';

    }



    render() {
        return (
            <div className="signup">
                <div className="signup-form">
                    <h1>SignUp</h1>
                    <form onSubmit={this.onSubmit}>
                        <label htmlFor="firstName">First Name</label>
                        <input type="text"  name="firstName" value={this.state.firstName} onChange={this.onChangeFirstName}/>
                        <label htmlFor="lastName">Last Name</label>
                        <input type="text"  name="lastName"  value={this.state.lastName} onChange={this.onChangeLastName}/>
                        <label htmlFor="email">Email</label>
                        <input type="email"  name="email"  value={this.state.email} onChange={this.onChangeEmail}/>
                        <label htmlFor="password">Password</label>
                        <input type="password"  value={this.state.password} onChange={this.onChangePassword}/>
                        <input type="submit" className="submit" value="submit"></input>
                    </form>
                </div>
            </div>
        )
    }
}
