import React, { Component } from 'react';
import '../App.css';
import Navbar from '../components/navbar'
import axios from 'axios'

export default class signup extends Component {


    constructor(props) {
        super(props);
    
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    
        this.state = {
          username: '',
          description: '',
          duration: 0,
          date: new Date(),
          users: []
        }
      }
    
      componentDidMount() {
        axios.get('http://localhost:5000/users/')
          .then(response => {
            if (response.data.length > 0) {
              this.setState({
                users: response.data.map(user => user.username),
                username: response.data[0].username
              })
            }
          })
          .catch((error) => {
            console.log(error);
          })
    
      }
    
      onChangeUsername(e) {
        this.setState({
          username: e.target.value
        })
      }
    
      onChangeDescription(e) {
        this.setState({
          description: e.target.value
        })
      }
    
      onChangeDuration(e) {
        this.setState({
          duration: e.target.value
        })
      }
    
      onChangeDate(date) {
        this.setState({
          date: date
        })
      }
    
      onSubmit(e) {
        e.preventDefault();
    
        const exercise = {
          username: this.state.username,
          description: this.state.description,
          duration: this.state.duration,
          date: this.state.date
        }
    
        console.log(exercise);
    
        axios.post('http://localhost:5000/exercises/add', exercise)
          .then(res => console.log(res.data));
    
        window.location = '/';

    }










    render() {
        return (
            <div>
                <Navbar></Navbar>
                <div className="signup-form">
                    <h1>SignUp</h1>
                    <form onSubmit="this.onSubmit">
                        <label htmlFor="firstName">First Name</label>
                        <input type="text"  name="firstName" value={this.state.firstName} onChange={this.onChangeFirstName}/>
                        <label htmlFor="lastName">Last Name</label>
                        <input type="text"  name="lastName"  value={this.state.lastName} onChange={this.onChangeLasttName}/>
                        <label htmlFor="email">Email</label>
                        <input type="email"  name="email"  value={this.state.email} onChange={this.onChangeEmail}/>
                        <label htmlFor="password">Password</label>
                        <input type="password"  name="password" value={this.state.password} onChange={this.onChangePassword}/>
                        <label htmlFor="confirmPassword">Confirm Password</label>
                        <input type="password"  name="confirmPassword" />
                        <input type="Submit" className="submit" value="submit"></input>
                    </form>
                </div>
            </div>
        )
    }
}
