import React, { Component } from 'react';
import '../App.css';
import axios from "axios";
import {Link} from 'react-router-dom';
import Footer from '../components/footer';



export default class login extends Component {
    // state ={
    //     userList: [],
    //     emailId:'',
    //     password:''

    // };  
    constructor(props) {
        super(props);

        this.onChangeEmailId = this.onChangeEmailId.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            userList: [],
            emailId: '',
            password: ''
        }
    }
    componentDidMount() {
        axios.get('http://localhost:5000/newUser')
            .then(res => {
                this.setState({ userList: res.data });
            })
            .catch((error) => {
                console.log(error);
            })
    }
    onChangeEmailId(e) {
        this.setState({
            emailId: e.target.value
        })
    }
    onChangePassword(e) {
        this.setState({
            password: e.target.value
        })
    }

    onSubmit(e) {
        e.preventDefault();

        this.state.userList.map((userList) => {
            if (this.state.emailId == userList.email && this.state.password == userList.password) {
                window.location = "/userDashboard";

            }
            else {
                // window.location = './admin'
            }

        }
        )
    }


    render() {
        return (
            <div className="login">

                <h1>Login Form</h1>
                <div className="login-form">

                    <form onSubmit={this.onSubmit}>
                        <label htmlFor="email">Email Id</label>
                        <input type="email" name="email" value={this.state.emailId} onChange={this.onChangeEmailId} />
                        <label htmlFor="password">Enter Password</label>
                        <input type="password" name="password" value={this.state.password} onChange={this.onChangePassword} />
                        <Link className="li" to="/signup">New User? Create Account.</Link>
                        <input type="submit" className="submit" value="Login" />


                    </form>
                </div>
                <Footer></Footer>
            </div>
        )
    }
}
