import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import Content3 from './content3';

class addDoctor extends Component {
    
    constructor(props) {
        super(props);
    
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeSpeciality = this.onChangeSpeciality.bind(this);
        this.onChangeTiming = this.onChangeTiming.bind(this);
        // this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    
        this.state = {
          name: '',
          speciality: '',
          timing: '',
          date: new Date(),
        }
      }
    
    //   componentDidMount() {
    //     axios.get('http://localhost:5000/users/')
    //       .then(response => {
    //         if (response.data.length > 0) {
    //           this.setState({
    //             users: response.data.map(user => user.username),
    //             username: response.data[0].username
    //           })
    //         }
    //       })
    //       .catch((error) => {
    //         console.log(error);
    //       })
    
    //   }
    
      onChangeName(e) {
        this.setState({
          name: e.target.value
        })
      }
    
      onChangeSpeciality(e) {
        this.setState({
          speciality: e.target.value
        })
      }
    
      onChangeTiming(e) {
        this.setState({
          timing: e.target.value
        })
      }
    
      onChangeDate(date) {
        this.setState({
          date: date
        })
      }
    
      onSubmit(e) {
        e.preventDefault();
    
        const doctor = {
          name: this.state.name,
          speciality: this.state.speciality,
          timing: this.state.timing,
          date: this.state.date
        }
    
        console.log(doctor);
    
        axios.post('http://localhost:5000/doctorList/add', doctor)
          .then(res => console.log(res.data));
    
        window.location = '/addDoctor';
      }





    render() {
        return (
           <div className="addDoctor">
                <div className='addDoctor-form'>
                    <h1>Enter Doctors details here</h1>
                    <form onSubmit={this.onSubmit}>
                        <label htmlFor="name">Name</label>
                        <input type="text" name="name" value={this.state.name} onChange={this.onChangeName} />
                        <label htmlFor="specilaity" >Specilaity</label>
                        <input type="text" name="speciality" value={this.state.speciality} onChange={this.onChangeSpeciality} />
                        <label htmlFor="timing">Timing</label>
                        <input type="number" name="timing" value={this.state.TIming} onChange={this.onChangeTiming} />
                        <input type="Submit" className="submit" value="submit"></input>
                    </form>
                </div>
                <div className="space">

                </div>
                <Content3></Content3>

                </div>
            
        )
    }
}

export default addDoctor;
