import react, { Component } from 'react';
import '../App.css';
import axios from 'axios';


export default class Modal extends Component {
    state ={
        x:0,
        doctor: [],
        slott: [],
        l: 'Aman'
    };

    componentDidMount() {
        
        axios.get('http://localhost:5000/doctorList')
            .then(res => {
                this.setState({doctor: res.data});
              

            }) 
            .catch((error) => {
                console.log(error);
            })
            axios.get('http://localhost:5000/doctorList/l')
            .then(res => {
                this.setState({slott: res.data.slot});

            })         
          
        } 

    slots = ["4:00 pm - 4:15 pm", "4:15 pm - 4:30 pm", "4:31 pm - 4:45 pm", "4:46 pm - 5:00 pm","5:00pm - 6:00pm "]
    render() {
        return (
            <div className="modal display-block">
                <section className="modal-main">
                    <div className="main-content">
                        <div className="modal-header">
                            <h1>Book Meeting for 15 minute</h1>
                            <button className="modal-button" type="button" onClick={this.props.handleClose}>
                                Close
                            </button>
                        </div>
                        <div className="slotContainer">
                            {this.state.slott.map((slott =>
                                <div className="slot">{slott}</div>
                            ))
                            }
                            {/* <p>{this.state.name}</p> */}
                        </div>
                    </div>

                </section>
            </div >
        );
    }
};
