import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';

class addDoctor extends Component {
    
    constructor(props) {
        super(props);
    
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    
        this.state = {
          username: '',
          description: '',
          duration: 0,
          date: new Date(),
          users: []
        }
      }
    
      componentDidMount() {
        axios.get('http://localhost:5000/users/')
          .then(response => {
            if (response.data.length > 0) {
              this.setState({
                users: response.data.map(user => user.username),
                username: response.data[0].username
              })
            }
          })
          .catch((error) => {
            console.log(error);
          })
    
      }
    
      onChangeUsername(e) {
        this.setState({
          username: e.target.value
        })
      }
    
      onChangeDescription(e) {
        this.setState({
          description: e.target.value
        })
      }
    
      onChangeDuration(e) {
        this.setState({
          duration: e.target.value
        })
      }
    
      onChangeDate(date) {
        this.setState({
          date: date
        })
      }
    
      onSubmit(e) {
        e.preventDefault();
    
        const exercise = {
          username: this.state.username,
          description: this.state.description,
          duration: this.state.duration,
          date: this.state.date
        }
    
        console.log(exercise);
    
        axios.post('http://localhost:5000/exercises/add', exercise)
          .then(res => console.log(res.data));
    
        window.location = '/';
      }





    render() {
        return (
                <div className='addDoctor-form'>
                    <h1>Enter Doctors details here</h1>
                    <form onSubmit={this.onSubmit}>
                        <label htmlFor="name">Name</label>
                        <input type="text" name="name" value={this.state.name} onChange={this.onChangeName} />
                        <label htmlFor="specilaity" >Specilaity</label>
                        <input type="text" name="speciality" value={this.state.speciality} onChange={this.onChangeSpeciality} />
                        <label htmlFor="timing">Timing</label>
                        <input type="text" name="timing" value={this.state.TIming} onChange={this.onChangeTiming} />
                        <input type="Submit" className="submit" value="submit"></input>
                    </form>
                </div>
            
        )
    }
}

export default addDoctor;
